function StatusBadge({ status }: { status: string }) {
  const palette: Record<string, string> = {
    PENDENTE: "bg-warning/10 text-warning",
    ATRASADA: "bg-danger/10 text-danger",
    CONFIRMADA: "bg-success/10 text-success",
  };
  return <span className={`rounded-full px-2 py-1 text-xs font-semibold ${palette[status] ?? "bg-muted/10 text-muted"}`}>{status}</span>;
}

function StatusPill({ status }: { status: string }) {
  const palette: Record<string, string> = {
    ATIVO: "bg-success/10 text-success",
    PAUSADO: "bg-warning/10 text-warning",
    ENCERRADO: "bg-muted/10 text-muted",
  };
  return (
    <span className={`rounded-full px-2 py-1 text-xs font-semibold ${palette[status] ?? "bg-muted/10 text-muted"}`}>
      {status}
    </span>
  );
}









